/**
 *
 *
 * @export
 * @class HttpError
 * @extends {Error}
 */
class HttpError extends Error {
  /**
   * Creates an instance of HttpError.
   * @param {string} message
   * @param {HttpResponse} response
   * @memberof HttpError
   */
  constructor(message, response) {
    super(message);
    this.response = response;
  }
}
export { HttpError };
